// padding : 10 ~ 500px by 10px
// --------------------------------------------------------------------------------------------------------------------------------------------
@padding: 10;
    .padding-loop(@index) when (@index < 501) {
      .padding-@{index} { padding: unit(@index, px); }
      .padding-loop(@index + 10);
    }
    // generate .padding-10 ~ 500px by 10px
    .padding-loop(@padding);

// padding-top : 10~ 500px by 10px 
// --------------------------------------------------------------------------------------------------------------------------------------------
@paddingTOP: 10;
    .paddingTOP-loop(@index) when (@index < 501) {
      .padding-top-@{index} { padding-top: unit(@index, px); }
      .paddingTOP-loop(@index + 10);
    }
    // generate .padding-top-10 ~ 500px by 10px
