// --------------------------------------------------------------------------------------------------------------------------------------------
.alink(@LinkColor, @HoverColor,@BorderColor) {
     color:@LinkColor;
     text-decoration: none;
     border-bottom: 1px solid @LinkColor;
     padding-bottom: 2px;
  &:hover,
  &:focus {
          color: @HoverColor;
          text-decoration: none;
          border-bottom: 1px solid @BorderColor;
          padding-bottom: 2px;
  }
}
